export var countCourse = function countCourse(sessionItems) {
  var result = [0, 0, 0];
  sessionItems.forEach(function (sessionItem) {
    var key = sessionItem.type.key;

    if (key === 'PRIVATE_COURSE') {
      result[0]++;
    }

    if (key === 'GROUP_COURSE') {
      result[1]++;
    }

    if (key === 'SUBSCRIPTION_COURSE') {
      result[2]++;
    }
  });
  return result;
};
