------------------------------------------
|
| 非空
|
------------------------------------------ */
// 输入框

export var NoEmptyInputRule = [{
  message: RequiredText,
  required: true,
  whitespace: true
}]; // 非输入框, 如日期, 单选, 级联, 包括数字

export var NoEmptyOtherRule = [{
  message: RequiredText,
  required: true
}];
/*
------------------------------------------
|
| 输入长度上限
|
------------------------------------------ */
// 输入框

export var InputLengthLimitRule = [{
  max: 500,
  message: LengthLimitedText
}]; // 文本域

export var TextAreaLengthLimitRule = [{
  max: 2000,
  message: LengthLimitedText
}]; // 文本域最大不能超过64位

export var InputLengthLimit64Rule = [{
  max: 64,
  message: OverMaxLengthText
}]; // 文本域不能为空且最大不能超过64位

export var NoEmptyAndMaxRule = [].concat(NoEmptyInputRule, InputLengthLimit64Rule);
/*
------------------------------------------
|
| 整合规则
|
------------------------------------------ */

export var PositiveIntegerRule = [{
  pattern: positiveIntegerReg,
  message: '请输入正整数'
}];
